CREATE TABLE IF NOT EXISTS Resources(ResourceID INT AUTO_INCREMENT,ResourceName VARCHAR(50),ResourceLink VARCHAR(500),MentorView TINYINT DEFAULT 0,primary key (ResourceID));
CREATE TABLE IF NOT EXISTS Mentee (mentee_username VARCHAR(50),password VARCHAR(255) NOT NULL,first_name VARCHAR(50),surname VARCHAR(50),email_address VARCHAR(50) NOT NULL,phone_number VARCHAR(50) NOT NULL, imagepath varchar(50) DEFAULT 'menteeupload/default.png', primary key (mentee_username));
CREATE TABLE IF NOT EXISTS Mentor (mentor_username VARCHAR(50),password VARCHAR(255) NOT NULL,first_name VARCHAR(50) NOT NULL,surname VARCHAR(50) NOT NULL,email_address VARCHAR(50) NOT NULL,phone_number VARCHAR(50) NOT NULL, imagepath varchar(50) DEFAULT 'mentorupload/default.png', hidden boolean not null default 0, primary key (mentor_username));
CREATE TABLE IF NOT EXISTS extra_mentee (mentee_username VARCHAR(50),bio VARCHAR(300),facebook_link VARCHAR(50),linkedin_link VARCHAR (50),selected_industry VARCHAR(50),primary key (mentee_username),foreign key (mentee_username) references Mentee (mentee_username)ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS extra_mentor (mentor_username VARCHAR(50),bio VARCHAR(300),facebook_link VARCHAR(50),linkedin_link VARCHAR (50),selected_industry VARCHAR(50),primary key (mentor_username),foreign key (mentor_username) references Mentor (mentor_username)ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS Matches(mentee_username VARCHAR(50),mentee_name varchar(100),mentor_username VARCHAR(50),mentor_name varchar(100),mentor_approval boolean default null,admin_approval boolean default null,foreign key (mentee_username) references Mentee (mentee_username) ON DELETE CASCADE,foreign key (mentor_username) references Mentor (mentor_username) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS Meetings(mentee_username VARCHAR(50),mentee_name VARCHAR(100),mentor_username VARCHAR(50),mentor_name VARCHAR(100),title varchar(50),description varchar(100),meetingdate DATE,starttime TIME,endtime TIME,approval TINYINT default 0,foreign key (mentee_username) references Mentee (mentee_username) ON DELETE CASCADE,foreign key (mentor_username) references Mentor (mentor_username)ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS Admin (username VARCHAR(50) NOT NULL, password VARCHAR(255) NOT NULL, email_address VARCHAR(50) NOT NULL, primary key (username));
CREATE TABLE IF NOT EXISTS menteeEducation (mentee_username VARCHAR(50),level VARCHAR(50),discipline VARCHAR(50),institution VARCHAR(50),foreign key (mentee_username) references Mentee (mentee_username) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS menteeExperience (mentee_username VARCHAR(50),role VARCHAR(50),organisation VARCHAR(50),industry VARCHAR(50),foreign key (mentee_username) references Mentee (mentee_username) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS menteeSkills (mentee_username VARCHAR(50),technical VARCHAR(50),interpersonal VARCHAR(50),foreign key (mentee_username) references Mentee (mentee_username) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS menteeInterest (mentee_username VARCHAR(50),interests VARCHAR(50),foreign key (mentee_username) references Mentee (mentee_username) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS mentorEducation (mentor_username VARCHAR(50),level VARCHAR(50),discipline VARCHAR(50),institution VARCHAR(50),foreign key (mentor_username) references Mentor (mentor_username) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS mentorExperience (mentor_username VARCHAR(50),role VARCHAR(50),organisation VARCHAR(50),industry VARCHAR(50),foreign key (mentor_username) references Mentor (mentor_username)ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS mentorSkills (mentor_username VARCHAR(50),technical VARCHAR(50),interpersonal VARCHAR(50),foreign key (mentor_username) references Mentor (mentor_username)ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS mentorInterest (mentor_username VARCHAR(50),interests VARCHAR(50),foreign key (mentor_username) references Mentor (mentor_username)ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS mentorPreference (mentor_username VARCHAR(50),capacity int,meetingTime int,timeAvailable VARCHAR(50),relationship VARCHAR(50), primary key (mentor_username),foreign key (mentor_username) references Mentor (mentor_username)ON DELETE CASCADE);
